        $param=<<currentTiddler>>
        foldedState=<<folded-state>>
      />
      {{$:/core/images/chevron-down}}
    </$button>
  </$reveal>
</$reveal>
"},"$:/plugins/oflg/fishing/fishhead":{"title":"$:/plugins/oflg/fishing/fishhead","tags":"$:/tags/ViewTemplate","list-after":"$:/core/ui/ViewTemplate/title","text":"<$reveal
