<$button
	popup=<<qualify "$:/state/popup/$:/plugins/oflg/fishing/fishingdeck">>
	tooltip={{{[{$:/language}removeprefix[$:/]addprefix[$:/plugins/oflg/fishing/]getindex[fishingtip]]~[[$:/plugins/oflg/fishing/languages/en-GB]getindex[fishingtip]]}}}
	aria-label={{{[{$:/language}removeprefix[$:/]addprefix[$:/plugins/oflg/fishing/]getindex[fishingtip]]~[[$:/plugins/oflg/fishing/languages/en-GB]getindex[fishingtip]]}}}
	class=<<tv-config-toolbar-class>>
	selectedClass="tc-selected"
>
	<span class="tc-image-down-arrow tc-image-button">
		{{$:/plugins/oflg/fishing/icon}}
	</span>
</$button>
<!-- creat fishingdeck -->
<$reveal
	state=<<qualify "$:/state/popup/$:/plugins/oflg/fishing/fishingdeck">>
	type="popup"
	position="below"
	animate="yes"
>
	<div
		class="tc-drop-down tc-popup-keep"
		style="min-width:0;"
	>
		<!-- creat fishingpond -->
		{{$:/plugins/oflg/fishing/fishingpond}}
		<!-- creat fishingrod -->
		{{$:/plugins/oflg/fishing/fishingrod}}
	</div>
</$reveal>"},"$:/plugins/oflg/fishing/fishingpond":{"title":"$:/plugins/oflg/fishing/fishingpond","text":"<$set
	name="fishingrod"
	value={{$:/temp/fishing!!fishingrod}}
	emptyValue="[tag[?]]"
>
	<$let
		due="[has[due]]-[has[due]!days:due[1]]+[sort[due]reverse[]sortsub:number<priority>reverse[]]"
		undue="[has[due]!days:due[1]sort[due]]"
		new="[!has[due]sort[created]]"
	>
		<$list
			filter="due undue new"
			variable="fishingpondname"
		>
			<$let
				fishingpond={{{[<fishingpondname>match[due]then<due>]~[<fishingpondname>match[undue]then<undue>]~[<fishingpondname>match[new]then<new>]}}}
			>
				<$button
					tooltip={{{[{$:/language}removeprefix[$:/]addprefix[$:/plugins/oflg/fishing/]getindex[fishingpondtip]]~[[$:/plugins/oflg/fishing/languages/en-GB]getindex[fishingpondtip]]}}}
					aria-label={{{[{$:/language}removeprefix[$:/]addprefix[$:/plugins/oflg/fishing/]getindex[fishingpondtip]]~[[$:/plugins/oflg/fishing/languages/en-GB]getindex[fishingpondtip]]}}}
					class="tc-btn-invisible tc-tiddlylink"
					style="padding-right:2px;"
				>
					<$action-setfield
						$tiddler="$:/temp/fishing"
						text=""
						fishingrod=<<fishingrod>>
						fishingpond=<<fishingpond>>
						fish={{{[subfilter<fishingrod>subfilter<fishingpond>first[]]}}}
					/>
					<$action-setfield
						$tiddler={{{[{$:/temp/fishing!!fish}addprefix[$:/state/folded/]]}}}
						text={{{[{$:/temp/fishing!!fish}has[due]!search:caption:literal[{{||Excerpt}}]then[hide]else[show]]}}}
					/>
					<$action-navigate
						$to={{$:/temp/fishing!!fish}}
					/>
					<$action-popup
						$state=<<qualify "$:/state/popup/$:/plugins/oflg/fishing/fishingdeck">>
						$coords=""
					/>
					<$text text={{{[{$:/language}removeprefix[$:/]addprefix[$:/plugins/oflg/fishing/]getindex<fishingpondname>]~[[$:/plugins/oflg/fishing/languages/en-GB]getindex<fishingpondname>]}}}/><$count filter="[subfilter<fishingrod>subfilter<fishingpond>]"/>
				</$button>
			</$let>
		</$list>
	</$let>
</$set>"},"$:/plugins/oflg/fishing/fishingrod":{"title":"$:/plugins/oflg/fishing/fishingrod","text":"<div
	style="margin:5px 15px;"
>
	<$edit-text
		tiddler="$:/temp/fishing"
		field="fishingrod"
		tag="input"
		focus="true"
		placeholder="[tag[?]]"
		inputActions='<$action-setfield $tiddler="$:/temp/fishing" fish=""/>'
	/>
</div>
<div
	style="display:flex;margin:0 15px;flex-flow:wrap;align-items:center;font-size:10pt;"
>
	<$reveal
		default={{{[all[tiddlers+shadows]removeprefix[$:/plugins/oflg/fishing/fishingrod/]count[]]}}}
		type="gt"
		text="0"
		animate="yes"
	>
		<$button
			tooltip={{{[{$:/language}removeprefix[$:/]addprefix[$:/plugins/oflg/fishing/]getindex[commonfiltertip]]~[[$:/plugins/oflg/fishing/languages/en-GB]getindex[commonfiltertip]]}}}
			aria-label={{{[{$:/language}removeprefix[$:/]addprefix[$:/plugins/oflg/fishing/]getindex[commonfiltertip]]~[[$:/plugins/oflg/fishing/languages/en-GB]getindex[commonfiltertip]]}}}
			popup=<<qualify "$:/state/popup/$:/plugins/oflg/fishing/fishingrod/fishingline">>
			class="tc-btn-invisible"
			style="margin:0;padding:0 5px;width:auto;height:auto;"
		>
			{{$:/core/images/down-arrow}}
		</$button>
	</$reveal>
	<$reveal
		state=<<qualify "$:/state/popup/$:/plugins/oflg/fishing/fishingrod/fishingline">>
		type="popup"
		position="below"
		animate="yes"
	>
		<div
			class="tc-drop-down"
			style="min-width:0;font-size:10pt;"
		>
			<$list
				filter="[all[tiddlers+shadows]removeprefix[$:/plugins/oflg/fishing/fishingrod/]sort[title]]"
				variable="fishingrod"
			>
				<$button
					tooltip={{{[{$:/language}removeprefix[$:/]addprefix[$:/plugins/oflg/fishing/]getindex[fillfiltertip]]~[[$:/plugins/oflg/fishing/languages/en-GB]getindex[fillfiltertip]]+[addsuffix<fishingrod>]}}}
					aria-label={{{[{$:/language}removeprefix[$:/]addprefix[$:/plugins/oflg/fishing/]getindex[fillfiltertip]]~[[$:/plugins/oflg/fishing/languages/en-GB]getindex[fillfiltertip]]+[addsuffix<fishingrod>]}}}
					popup=<<qualify "$:/state/popup/$:/plugins/oflg/fishing/fishingrod/fishingline">>
					class="tc-btn-invisible"
				>
					<$action-setfield
						$tiddler="$:/temp/fishing"
						fishingrod=<<fishingrod>>
					/>
					<$text text=<<fishingrod>>/>
				</$button>
			</$list>
		</div>
	</$reveal>
	<$reveal
		default={{{[{$:/temp/fishing!!fishingrod}]}}}
		type="nomatch"
		text=""
		animate="yes"
	>
		<$reveal
			default={{{[{$:/temp/fishing!!fishingrod}addprefix[$:/plugins/oflg/fishing/fishingrod/]is[missing]]}}}
			type="nomatch"
			text=""
			animate="yes"
		>
			<$button
				tooltip={{{[{$:/language}removeprefix[$:/]addprefix[$:/plugins/oflg/fishing/]getindex[pinfiltertip]]~[[$:/plugins/oflg/fishing/languages/en-GB]getindex[pinfiltertip]]+[addsuffix{$:/temp/fishing!!fishingrod}]}}}
				aria-label={{{[{$:/language}removeprefix[$:/]addprefix[$:/plugins/oflg/fishing/]getindex[pinfiltertip]]~[[$:/plugins/oflg/fishing/languages/en-GB]getindex[pinfiltertip]]+[addsuffix{$:/temp/fishing!!fishingrod}]}}}
				class="tc-btn-invisible" style="margin:0;padding:0 5px;"
			>
				<$action-setfield
					$tiddler={{{[{$:/temp/fishing!!fishingrod}addprefix[$:/plugins/oflg/fishing/fishingrod/]]}}}
					type="application/json"
				/>
				{{$:/core/images/new-button}}
			</$button>
		</$reveal>
		<$reveal
			default={{{[{$:/temp/fishing!!fishingrod}addprefix[$:/plugins/oflg/fishing/fishingrod/]is[missing]]}}}
			type="match"
			text=""
			animate="yes"
		>
			<$button
				tooltip={{{[{$:/language}removeprefix[$:/]addprefix[$:/plugins/oflg/fishing/]getindex[unpinfiltertip]]~[[$:/plugins/oflg/fishing/languages/en-GB]getindex[unpinfiltertip]]+[addsuffix{$:/temp/fishing!!fishingrod}]}}}
				aria-label={{{[{$:/language}removeprefix[$:/]addprefix[$:/plugins/oflg/fishing/]getindex[unpinfiltertip]]~[[$:/plugins/oflg/fishing/languages/en-GB]getindex[unpinfiltertip]]+[addsuffix{$:/temp/fishing!!fishingrod}]}}}
				class="tc-btn-invisible"
				style="margin:0;padding:0 5px;"
			>
				<$action-deletetiddler
					$tiddler={{{[{$:/temp/fishing!!fishingrod}addprefix[$:/plugins/oflg/fishing/fishingrod/]]}}}
				/>
				<$action-setfield
					$tiddler="$:/temp/fishing"
					fishingrod=""
				/>
				{{$:/core/images/delete-button}}
			</$button>
		</$reveal>
	</$reveal>
</div>"},"$:/plugins/oflg/fishing/icon":{"title":"$:/plugins/oflg/fishing/icon","tags":"$:/tags/Image","text":"<svg
