"},"$:/core/ui/ViewTemplate/title/fish":{"title":"$:/core/ui/ViewTemplate/title/fish","text":""},"Answer":{"title":"Answer","tags":"question","text":"{{!!title}}
<$reveal
	default={{$:/temp/fishing!!fish}}
	type="match"
	text=<<currentTiddler>>
	animate="yes"
>
	<$edit
		tiddler="$:/temp/fishing"
		placeholder={{$:/language/EditTemplate/Body/Placeholder}}
		minHeight="15px"
		class="tc-edit-texteditor tc-edit-texteditor-body"
	>
		<$set
			value=<<currentTiddler>>
			name="targetTiddler"
		>
			<$list
				filter="[all[shadows+tiddlers]tag[$:/tags/EditorToolbar]!has[draft.of]]"
			>
				<$reveal
					type="nomatch"
					state=<<config-visibility-title>>
					text="hide"
					class="tc-text-editor-toolbar-item-wrapper"
					style="display: none;"
				>
					<$transclude
						tiddler="$:/core/ui/EditTemplate/body/toolbar/button"
						mode="inline"
					/>
				</$reveal>
			</$list>
			<$list
				filter="[all[shadows+tiddlers]tag[$:/tags/EditorTools]!has[draft.of]]"
			>
				<$list
					filter={{!!condition}}
					variable="list-condition"
				>
					<$transclude/>
				</$list>
			</$list>
		</$set>
	</$edit>
	<$reveal
		default={{$:/temp/fishing}}
		type="nomatch"
		text=""
		animate="yes"
	>
		<$reveal
			state=<<folded-state>>
			type="match"
			text="show"
			animate="yes"
		>
			<$diff-text
				source={{$:/temp/fishing}}
				dest={{!!text}}
			/>
		</$reveal>
	</$reveal>
</$reveal>"},"Cloze":{"title":"Cloze","tags":"question","text":"<$reveal
	state=<<folded-state>>
	type="nomatch"
	text="show"
	animate="yes"
>
	<$reveal
		default={{$:/temp/fishing!!fish}}
		type="nomatch"
		text=<<currentTiddler>>
		animate="yes"
	>
		{{!!title}}
	</$reveal>
	<$reveal
		default={{$:/temp/fishing!!fish}}
		type="match"
		text=<<currentTiddler>>
		animate="yes"
	>
		<$reveal
			default={{{[{!!title}split[__]count[]]}}}
			type="gteq"
			text="3"
			animate="yes"
		>
			<$list
				filter="[{!!title}split[__]]"
				variable="titleitem"
				counter="counter"
			>
				<$reveal
					default={{{[<counter>remainder[2]]}}}
					type="nomatch"
					text="0"
				>
					<<titleitem>>
				</$reveal>
				<$reveal
					default={{{[<counter>remainder[2]]}}}
					type="match"
					text="0"
				>
					<$let
						clozeid=<<counter>>
						clozecontent=<<titleitem>>
						clozecontent={{{[[$:/temp/fishing]getindex<clozeid>match[show]then<titleitem>else[`    `]]}}}
					>
						<$button
							class="tc-btn-invisible tc-tiddlylink"
						>
							<$action-setfield
								$tiddler="$:/temp/fishing"
								$index=<<clozeid>>
								$value={{{[[$:/temp/fishing]getindex<clozeid>match[show]then[]else[show]]}}}
							/>
								<<clozecontent>>
						</$button>
					</$let>
				</$reveal>
			</$list>
		</$reveal>
		<$reveal
			default={{{[{!!title}split[__]count[]]}}}
			type="lt"
			text="3"
			animate="yes"
		>
			{{!!title}}
		</$reveal>
	</$reveal>
</$reveal>
<$reveal
	state=<<folded-state>>
	type="match"
	text="show"
	animate="yes"
>
	{{!!title}}
</$reveal>"},"Excerpt":{"title":"Excerpt","text":"<$reveal
