\define sidebar-open-count()
<$vars
    uri=<<makedatauri '$(currentTiddler)$' "text/plain">>
    urititle="""{{{ [<uri>removeprefix[data:text/plain,]] }}}"""
>
.tc-sidebar-tab-open-item a[href="#<<urititle>>"]:before { content: counter(section);  }
</$vars>
\end

<pre>
/*** COUNTER in SIDEBAR OPEN  ***/
.tc-story-river, .tc-sidebar-tab-open {
  counter-reset: section;
}
.tc-sidebar-tab-open-item [href]:before {
  position:absolute; margin-left:-2.5em;
  counter-increment: section;
}
<$list filter="[{$:/plugins/TWaddle/TiddlerSets/Stylesheet/toggles!!counter}match[true]]">
<$list filter="""[tag[TiddlerSet]get[list]enlist-input[]] :intersection[enlist{$:/StoryList!!list}]"""><<sidebar-open-count>></$list>
</$list>

/*** COUNTER IN RIVER ***/
<$vars  prefix="""[data-tiddler-title='""" suffix="""']""">
<$list filter="""[tag[TiddlerSet]get[list]enlist-input[]addprefix<prefix>addsuffix<suffix>]""" variable=tid>
<$list filter="[{$:/plugins/TWaddle/TiddlerSets/Stylesheet/toggles!!counter}match[true]]">
<<tid>> .count::before {
  content: counter(section) ;
  counter-increment: section;
  position:absolute; /* let title slip up beneath */
  color: white;
  background: #555; /* does not appear in print - ? */
  border: 5px solid #555; 
  padding: 5px 10px;
  border-radius: 50%;
  font-size:1.5em;
  margin-top: 5px;
  font-weight:bold;
}
<<tid>> .tc-tiddler-title {
  margin-left:4em;
}
</$list>
/*** HIDING OF FIELDS ***/
<$list filter="[enlist{$:/plugins/TWaddle/TiddlerSets/Stylesheet/toggles!!fields-to-hide}]"><<tid>> {{!!title}} {
  display:none;
}
</$list></$list>
</$vars>
</pre>

@media print { /* class to hide div from printing */
  .tiddlersets-noprint { display:none }
}
.tiddlersets-navigate-btn:focus {
  outline:0;
}
/*Table showing the TiddlerSet's list field contents*/
.tiddlersets-list-table-editor {
  width:100%;
  border:0;
  height:100%
}

.tiddlerset-list-table {
  padding:0;
  width:12em;
}
.tiddlerset-list-table > div {
  text-align:center;
  background:#f0f0f0;
  font-weight:bold;
  border:1px solid #ddd;
  line-height:1.5em;
}

/* The "list-links-draggable" bullet list displayed as */
/* a table inside the surrounding table cell */
.tiddlerset-list-table ul {
  margin:0;
  display:table;
  width:100%; 
}
.tiddlerset-list-table .tc-btn-invisible.tc-btn-mini svg {
  font-size: 0.7em;
  fill: <<colour muted-foreground>>;
  margin-left:5px;
}
.tiddlerset-list-table .tc-droppable {
  display:table-row;
}
.tiddlerset-list-table .tc-droppable div:nth-of-type(2) {
  display:table-cell;
  height:3em;
  width:100% !important;
  border: 1px solid #ddd;  border-bottom:0;
}
.tiddlerset-list-table > span > ul > div {
  display:none !important;
}
